import React, { useState } from 'react';
import { ServiceItems } from './ServiceItems'

const About = () => {
    return ( 
        <section className="about">
            <div className="story">
                <h1>Story</h1>
                <article >
                    <div>
                        <img src={ process.env.PUBLIC_URL +  "/assets/story1.png" } alt="company img"/>
                    </div>
                    <p>
                        Toaru is a leading IT company establised in 2005 by John in India. After getting huge success, John expands his business to Japan in 2010. Toaru has helped more than 1000 Japanese middle scale companies solve problems and expand thier overseas business. 
                    </p>
                </article>
            </div>
            <div className="value">
            </div>

                <ServiceItems />

        </section> 
    );
}
export default About;
