import React from 'react';


const Footer = () => {
    function makeUpperCase(msg){
        return msg.toUpperCase()
    }
    const copyInfo = 'all right reserved'
    const companyInfo = '〒606-8393 Kyoto, Sakyo Ward, Higashitakeyacho, 50'
    return (
        <footer className="footer">
            <div className="footer_name">
                <span>トアル株式会社</span>
                <span>TOARU CORP.</span>

            </div>  

            <div className="footer-info">
                <strong><a href="https://goo.gl/maps/N1hncwgvdcSZM7tp9">{makeUpperCase(companyInfo)}</a></strong>
                <span>{makeUpperCase(copyInfo)}</span>
            </div>
        </footer>
    );
}

export default Footer;
